in vec4 vertexColor;
in vec2 uv0;
uniform sampler2D cc_spriteTexture;
layout(std140) uniform Constant {
  float _PixelNumber;
};
vec4 frag() {
  vec2 uv = floor(uv0 * _PixelNumber) / _PixelNumber;
  vec4 color = texture(cc_spriteTexture, uv);
  color *= vertexColor;
  ALPHA_TEST(color);
  return color;
